      background: @surface0 !important;
    }

    /* Privacy center */

    .privacy-center-root__number-item {
      background: @accent;
      color: @base;
    }

    .privacy-center-accordion {
      border-color: @accent;
    }

    .privacy-center-home-tabs {
      color: @base !important;
      &[aria-selected="true"],
      &:hover {
        color: @surface1 !important;
      }
