    pre,
    code {
      background-color: @mantle;
      color: @text;
      border-color: @surface0;
    }

    thead tr {
      border-color: @surface0;
    }

    .bitcoinAddress {
      background-color: @mantle;
      color: @text;
    }

    .bg-info {
      background-color: @mantle;
      color: @text;
    }

    .subscriptions {
      .rpm10 {
        border-color: @teal;

        h4,
        h5,
        .price {
          background-color: @teal;
          color: @base;
        }
      }

      .rpm50 {
        border-color: @green;

        h4,
        h5,
        .price {
          background-color: @green;
          color: @base;
        }
      }

      .rpm100 {
        border-color: @yellow;

        h4,
        h5,
        .price {
          background-color: @yellow;
          color: @base;
        }
      }

      .rpm500 {
        border-color: @peach;

        h4,
        h5,
        .price {
          background-color: @peach;
          color: @base;
        }
      }

      .description {
        color: @text;
      }
    }

    .finePrint {
      color: @text;
    }

    .table-bordered {
      &,
      th,
      td {
        border-color: @surface0;
      }
    }

    .dropdown-menu {
      background-color: @base;

      > li > a {
        &:hover,
        &:focus {
          background-color: @mantle;
        }
      }
    }

    @media (min-width: 768px) {
      ul.nav li.dropdown:hover {
        background-color: @base;
      }
    }

    @media (max-width: 480px) {
      #searchContainer {
        background-color: @mantle;
      }
    }
  }
}

