            @subtext0: #6c6f85;
            @overlay2: #7c7f93;
            @overlay1: #8c8fa1;
            @overlay0: #9ca0b0;
            @surface2: #acb0be;
            @surface1: #bcc0cc;
            @surface0: #ccd0da;
